mapper.select:
/* selectNotiList */ SELECT A.noti_id , A.noti_title , A.noti_contents , A.rdcnt , A.top_status , A.atch_link_id , A.create_dttm , A.modify_dttm , A.create_id , A.modify_id FROM ( SELECT a.noti_id , a.noti_title , a.noti_contents , a.rdcnt , a.top_status , '' AS ATCH_LINK_ID , date_format(a.create_dttm,'%Y-%m-%d') AS create_dttm , date_format(a.modify_dttm,'%Y-%m-%d') AS modify_dttm , a.create_id , a.modify_id FROM PTL_NOTI a WHERE 1 = 1 AND TOP_STATUS = '1' AND DEL_YN = 'N'
AND a.noti_title like concat('%',#{searchword},'%')
UNION ALL SELECT a.noti_id , a.noti_title , a.noti_contents , a.rdcnt , a.top_status , '' AS ATCH_LINK_ID , date_format(a.create_dttm,'%Y-%m-%d') AS create_dttm , date_format(a.modify_dttm,'%Y-%m-%d') AS modify_dttm , a.create_id , a.modify_id FROM PTL_NOTI a WHERE 1 = 1 AND TOP_STATUS = '0' AND DEL_YN = 'N'
AND a.noti_title like concat('%',#{searchword},'%')
) A , (SELECT @rownum:= #{rownum, jdbcType=INTEGER}) B ORDER BY A.TOP_STATUS desc , A.noti_id DESC
