mapper.select:
/* selectProjEvalList */
SELECT D.YEAR_EVAL_ID , D.YEAR_END_GB , D.YEAR_EVAL_GB , D.YEAR_END_EVAL_GB , D.YEAR_EVAL_FILE_GROUP , FN_USER_KEY_INFO(D.CREATE_ID , 'NM') AS USERNAME , DATE_FORMAT(D.CREATE_DTTM,'%Y-%m-%d') as CREATE_DTTM , A.PROJ_ID , A.PROJ_YEAR_ID , A.PROJ_YEAR_GB , A.PROJ_STEP_GB , A.PROJ_RECPT_NO , A.PROJ_TYPE_CD , A.RND_GB , A.TOT_COST , A.GOV_COST , A.CASH , A.STOCK , A.TECH_CLS_CD1 , (SELECT D.TCLS_NM FROM PTL_TECH_CLS_CODE D WHERE D.TCLS_ID = B.TECH_CLS_CD1 ) AS TECH_CLS_NM1 , A.WEIGHT1 , A.TECH_CLS_CD2 , (SELECT D.TCLS_NM FROM PTL_TECH_CLS_CODE D WHERE D.TCLS_ID = B.TECH_CLS_CD2 ) AS TECH_CLS_NM2 , A.WEIGHT2 , A.TECH_CLS_CD3 , (SELECT D.TCLS_NM FROM PTL_TECH_CLS_CODE D WHERE D.TCLS_ID = B.TECH_CLS_CD3 ) AS TECH_CLS_NM3 , A.WEIGHT3 , A.SECURTY_LEVL_CD , A.PROJ_NM_KOR , A.PROJ_NM_ENG , DATE_FORMAT(A.CUR_STRTDT,'%Y-%m-%d') AS CUR_STRTDT , DATE_FORMAT(A.CUR_ENDDT,'%Y-%m-%d') AS CUR_ENDDT , A.CREATE_ID , A.YEAR_FILE_GROUP , B.LEAD_ORG_CD , B.AGMT_GB , DATE_FORMAT(B.AGMT_DT,'%Y-%m-%d') AS AGMT_DT , (SELECT D.ORG_NM FROM PTL_ORG_MNG D WHERE D.ORG_ID = B.LEAD_ORG_CD ) AS ORG_NM , (SELECT D.ORG_CRM_NO FROM PTL_ORG_MNG D WHERE D.ORG_ID = B.LEAD_ORG_CD ) AS ORG_CRM_NO , (SELECT D.ORG_ADDRESS FROM PTL_ORG_MNG D WHERE D.ORG_ID = B.LEAD_ORG_CD ) AS ORG_ADDRESS , C.ANN_ID , C.ANN_NO , C.ANN_DT , C.ANN_NM , C.DEPT_ORG , C.DDCT_ORG , C.FILE_GROUP FROM PTL_PROJECT_YEAR A LEFT JOIN PTL_PROJECT B ON A.PROJ_ID = B.PROJ_ID LEFT JOIN PTL_ANNOUNCE C ON B.ANN_ID = C.ANN_ID LEFT JOIN PTL_YEAR_EVALUATE D ON A.PROJ_YEAR_ID = D.PROJ_YEAR_ID INNER JOIN (SELECT @rownum:= #{rownum, jdbcType=INTEGER}) R WHERE 1=1 AND A.DEL_YN ='N' AND B.AGMT_GB = '1'
AND C.DEPT_ORG = #{dept_org}
AND C.DDCT_ORG = #{ddct_org}
AND SUBSTRING(A.CUR_STRTDT, 1, 4) = #{proj_year}
AND A.PROJ_NM_KOR LIKE CONCAT('%', #{searchword}, '%')
ORDER BY A.CUR_STRTDT DESC, A.PROJ_YEAR_ID DESC

/* selectProjEvalListCount */ SELECT COUNT(1) AS CNT FROM ptl_project_year A LEFT JOIN PTL_PROJECT B ON A.PROJ_ID = B.PROJ_ID LEFT JOIN PTL_ANNOUNCE C ON B.ANN_ID = C.ANN_ID LEFT JOIN PTL_YEAR_EVALUATE D ON A.PROJ_YEAR_ID = D.PROJ_YEAR_ID WHERE 1=1 AND A.DEL_YN ='N' AND B.AGMT_GB = '1'
AND C.DEPT_ORG = #{dept_org}
AND C.DDCT_ORG = #{ddct_org}
AND SUBSTRING(A.CUR_STRTDT, 1, 4) = #{proj_year}
AND A.PROJ_NM_KOR LIKE CONCAT('%', #{searchword}, '%')

mapper.insert:
/* insertProjEval */
	INSERT INTO PTL_YEAR_EVALUATE (
		  YEAR_EVAL_ID
		, PROJ_YEAR_ID
		, YEAR_END_GB
		, YEAR_EVAL_GB
		, YEAR_END_EVAL_GB
		, YEAR_EVAL_FILE_GROUP
		, CREATE_ID
		, CREATE_DTTM
	) VALUES (
		  (SELECT CONCAT('EVAL_' , LPAD(NEXTVAL(PTL_YEAR_EVALUATE_SEQ),15,0 )) FROM DUAL)
		, #{proj_year_id, jdbcType = VARCHAR}
		, #{year_end_gb, jdbcType = VARCHAR}
		, #{year_eval_gb, jdbcType = VARCHAR}
		, #{year_end_eval_gb, jdbcType = VARCHAR}
		, #{year_eval_file_group, jdbcType = VARCHAR}
		, #{create_id, jdbcType = VARCHAR}
		, NOW()
	)ON DUPLICATE KEY UPDATE
		    YEAR_END_GB = #{year_end_gb,  jdbcType = VARCHAR}
		  ,	YEAR_EVAL_GB = #{year_eval_gb,  jdbcType = VARCHAR}
		  ,	YEAR_END_EVAL_GB = #{year_end_eval_gb,  jdbcType = VARCHAR}
		  , MODIFY_ID = #{modify_id,  jdbcType = VARCHAR}
		  , MODIFY_DTTM = NOW()
