mapper.select:
/* selectEndArticleListCount */ 
	SELECT  COUNT(1) AS CNT
      FROM  PTL_END_FOLLOW_ARTICLE
	 WHERE  1=1
	   AND  PROJ_END_ID = #{proj_end_id}

mapper.insert:
/* insertEndArticle */
	  INSERT INTO PTL_END_FOLLOW_ARTICLE (
			  END_ARTCL_ID
			, PROJ_END_ID
			, END_ARTCL_NM
			, END_ARTCL_AUTH
			, END_JOUNAL_INFO
			, END_ARTCL_SCI_GB
			, END_ARTCL_DOMESTIC_GB
			, CREATE_ID
			, CREATE_DTTM
		) VALUES (
			
				
					#{end_artcl_id, jdbcType=VARCHAR}	
						
				
					NEXTVAL(PTL_END_FOLLOW_ARTICLE_SEQ)
							
			
			, #{proj_end_id_article, jdbcType=VARCHAR}	
			, #{end_artcl_nm, jdbcType=VARCHAR}	
			, #{end_artcl_auth, jdbcType=VARCHAR}	
			, #{end_jounal_info, jdbcType=VARCHAR}	
			, #{end_artcl_sci_gb, jdbcType=VARCHAR}	
			, #{end_artcl_domestic_gb, jdbcType=VARCHAR}	
			, #{create_id, jdbcType=VARCHAR}	
			, NOW()
	   ) ON DUPLICATE KEY UPDATE
			  END_ARTCL_NM = #{end_artcl_nm, jdbcType=VARCHAR}	
			, END_ARTCL_AUTH = #{end_artcl_auth, jdbcType=VARCHAR}
			, END_JOUNAL_INFO = #{end_jounal_info, jdbcType=VARCHAR}
			, END_ARTCL_SCI_GB = #{end_artcl_sci_gb, jdbcType=VARCHAR}
			, END_ARTCL_DOMESTIC_GB = #{end_artcl_domestic_gb, jdbcType=VARCHAR}
			, MODIFY_ID = #{modify_id, jdbcType=VARCHAR}
			, MODIFY_DTTM = NOW()

mapper.delete:
/* deleteEndArticle */
	DELETE
	  FROM  PTL_END_FOLLOW_ARTICLE
	 WHERE  1=1
	   AND  END_ARTCL_ID = #{end_artcl_id}
