mapper.select:
	/* selectYearEtcListCount */ 
		SELECT  COUNT(1) AS CNT
	      FROM  PTL_YEAR_FOLLOW_ETC
 		 WHERE  1=1  
 		   AND  PROJ_YEAR_ID = #{proj_year_id}

mapper.insert:
/* insertYearEtc */
	 INSERT INTO PTL_YEAR_FOLLOW_ETC (
				  YEAR_ETC_ID
				, PROJ_YEAR_ID
				, YEAR_ETC_NM
				, YEAR_ETC_CN
				, YEAR_ETC_GB
				, YEAR_ETC_INFO
				, CREATE_ID
				, CREATE_DTTM
			) VALUES (
				
					
						#{year_etc_id, jdbcType=VARCHAR}	
							
					
						NEXTVAL(PTL_YEAR_FOLLOW_ETC_SEQ)
								
				
				, #{proj_year_id_etc, jdbcType=VARCHAR}	
				, #{year_etc_nm, jdbcType=VARCHAR}	
				, #{year_etc_cn, jdbcType=VARCHAR}	
				, #{year_etc_gb, jdbcType=VARCHAR}	
				, #{year_etc_info, jdbcType=VARCHAR}	
				, #{create_id, jdbcType=VARCHAR}	
				, NOW()
			) ON DUPLICATE KEY UPDATE
				  YEAR_ETC_NM = #{year_etc_nm, jdbcType=VARCHAR}	
				, YEAR_ETC_CN = #{year_etc_cn, jdbcType=VARCHAR}	
				, YEAR_ETC_GB = #{year_etc_gb, jdbcType=VARCHAR}	
				, YEAR_ETC_INFO = #{year_etc_info, jdbcType=VARCHAR}	
				, MODIFY_ID = #{modify_id, jdbcType=VARCHAR}
				, MODIFY_DTTM = NOW()

mapper.delete:
/* deleteYearEtc */
	DELETE
	  FROM  PTL_YEAR_FOLLOW_ETC
	 WHERE  1=1
	   AND  YEAR_ETC_ID = #{year_etc_id}
