mapper.select:
	/* selectEndEtcListCount */ 
		SELECT  COUNT(1) AS CNT
	      FROM  PTL_END_FOLLOW_ETC
 		 WHERE  1=1  
 		   AND  PROJ_END_ID = #{proj_end_id}

mapper.insert:
/* insertEndEtc */
	 INSERT INTO PTL_END_FOLLOW_ETC (
				  END_ETC_ID
				, PROJ_END_ID
				, END_ETC_NM
				, END_ETC_CN
				, END_ETC_GB
				, END_ETC_INFO
				, CREATE_ID
				, CREATE_DTTM
			) VALUES (
				
					
						#{end_etc_id, jdbcType=VARCHAR}	
							
					
						NEXTVAL(PTL_END_FOLLOW_ETC_SEQ)
								
				
				, #{proj_end_id_etc, jdbcType=VARCHAR}	
				, #{end_etc_nm, jdbcType=VARCHAR}	
				, #{end_etc_cn, jdbcType=VARCHAR}	
				, #{end_etc_gb, jdbcType=VARCHAR}	
				, #{end_etc_info, jdbcType=VARCHAR}	
				, #{create_id, jdbcType=VARCHAR}	
				, NOW()
			) ON DUPLICATE KEY UPDATE
				  END_ETC_NM = #{end_etc_nm, jdbcType=VARCHAR}	
				, END_ETC_CN = #{end_etc_cn, jdbcType=VARCHAR}	
				, END_ETC_GB = #{end_etc_gb, jdbcType=VARCHAR}	
				, END_ETC_INFO = #{end_etc_info, jdbcType=VARCHAR}	
				, MODIFY_ID = #{modify_id, jdbcType=VARCHAR}
				, MODIFY_DTTM = NOW()

mapper.delete:
/* deleteEndEtc */
	DELETE
	  FROM  PTL_END_FOLLOW_ETC
	 WHERE  1=1
	   AND  END_ETC_ID = #{end_etc_id}
