mapper.select:
	/* selectEndIpListCount */ 
		SELECT  COUNT(1) AS CNT
	      FROM  PTL_END_FOLLOW_IP
 		 WHERE  1=1  
 		   AND  PROJ_END_ID = #{proj_end_id}

mapper.insert:
/* insertEndIp */
	 INSERT INTO PTL_END_FOLLOW_IP (
				  END_IP_ID
				, PROJ_END_ID
				, END_IP_NM
				, END_IP_NO
				, END_IP_DT
				, END_IP_GB
				, END_IP_DOMESTIC_GB
				, CREATE_ID
				, CREATE_DTTM
			) VALUES (
				
					
						#{end_ip_id, jdbcType=VARCHAR}	
							
					
						NEXTVAL(PTL_END_FOLLOW_IP_SEQ)
								
				
				, #{proj_end_id_ip, jdbcType=VARCHAR}	
				, #{end_ip_nm, jdbcType=VARCHAR}
				, #{end_ip_no, jdbcType=VARCHAR}
				, (CASE WHEN DATE_FORMAT(STR_TO_DATE(#{end_ip_dt, jdbcType=VARCHAR}, '%Y-%m-%d'), '%Y%m%d') = '00000000' THEN '' 
				       ELSE DATE_FORMAT(STR_TO_DATE(#{end_ip_dt, jdbcType=VARCHAR}, '%Y-%m-%d'), '%Y%m%d') 
				   END) 
				, #{end_ip_gb, jdbcType=VARCHAR}
				, #{end_ip_domestic_gb, jdbcType=VARCHAR}
				, #{create_id, jdbcType=VARCHAR}
				, NOW()
			) ON DUPLICATE KEY UPDATE
				  END_IP_NM = #{end_ip_nm, jdbcType=VARCHAR}	
				, END_IP_NO = #{end_ip_no, jdbcType=VARCHAR}
				, END_IP_DT = (CASE WHEN DATE_FORMAT(STR_TO_DATE(#{end_ip_dt, jdbcType=VARCHAR}, '%Y-%m-%d'), '%Y%m%d') = '00000000' THEN '' 
				       				ELSE DATE_FORMAT(STR_TO_DATE(#{end_ip_dt, jdbcType=VARCHAR}, '%Y-%m-%d'), '%Y%m%d') 
				  			   END) 
				, END_IP_GB = #{end_ip_gb, jdbcType=VARCHAR}
				, end_IP_DOMESTIC_GB = #{end_ip_domestic_gb, jdbcType=VARCHAR}
				, MODIFY_ID = #{create_id, jdbcType=VARCHAR}
				, MODIFY_DTTM = NOW()

mapper.delete:
/* deleteEndIp */
	DELETE
	  FROM  PTL_END_FOLLOW_IP
	 WHERE  1=1
	   AND  END_IP_ID = #{end_ip_id}
