mapper.update:
UPDATE PTL_QNA SET 
	  QNA_ANSWER = #{qna_answer, jdbcType = VARCHAR}					
	, ANSWER_DTTM = now()
	, ANSWER_ID =  #{answer_id, jdbcType = VARCHAR}	
WHERE 1 = 1 
	AND QNA_ID =  #{qna_id, jdbcType=VARCHAR}

mapper.delete:
UPDATE   PTL_QNA SET 
	DEL_YN = 'Y'
 WHERE QNA_ID = #{qna_id, jdbcType=VARCHAR}
